mapper.select:
SELECT app_id, service_name, display_name, description, status, created_at, updated_at FROM lh_application
status = #{status}
ORDER BY created_at DESC

SELECT app_id, service_name, display_name, description, status, created_at, updated_at
FROM lh_application
WHERE app_id = #{appId}

SELECT app_id, service_name, display_name, description, status, created_at, updated_at
FROM lh_application
WHERE service_name = #{serviceName}
